/*
*
* Object of e-Cashibill
*
* A more detailed explanation can be found from the link below.
* - https://docs.popbill.com/en/cashbill/java/api#Cashbill
*
*/

{
    // Cashbill ID
    "mgtKey" : "20221103-001",

    // The date and time of trading (Format : yyyyMMddHHmmss)
    // └ Valid Value : Today(Default) / Previous day
    "tradeDT" : "20221103140812",

    // Document Type
    // └ Valid Value : 승인거래 / 취소거래
    // └ 승인거래 = Approval(General), 취소거래 = Revocation(Cancellation)
    "tradeType" : "승인거래",

 	// Issuance Purpose
 	// └ Valid Value : 소득공제용 / 지출증빙용
 	// └ 소득공제용 = To deduct income tax, 지출증빙용 = To proof the expenditure in business
    "tradeUsage" : "소득공제용",

 	// Trade Type
 	// └ Valid Value : 일반 / 도서공연 / 대중교통
 	// └ 일반 = General(Default), 도서공연 = Book and Performance, 대중교통 = Public Transportation
    "tradeOpt" : "일반",

    // Taxation Type
    // └ Valid Value : 과세 / 비과세
    // └ 과세 = Taxable, 비과세 = Non-taxable
    "taxationType" : "과세",

    // Total Amount
    // └ Sum of {@no.11 supplyCost}, {@no.12 tax} and {@no.13 serviceFee}
    // └ Only positive(+) number is available
    "totalAmount" : "11000",

    // Supply values
    // └ Only positive(+) number is available
    "supplyCost" : "10000",

    // Tax Amount
    // └ Only positive(+) number is available
    "tax" : "1000",

    // Service Fee
    // └ Only positive(+) number is available
    "serviceFee" : "0",

    // [Seller] Business Registration Number of user (10 digits except ‘-’)
    "franchiseCorpNum" : "1234567890",

    // [Seller] Identification Number for minor place of business
    // └ 4 digits number
    "franchiseTaxRegID" : "",

    // [Seller] Name of company
    "franchiseCorpName" : "Name of company",

    // [Seller] Name of representative
    "franchiseCEOName" : "Name of representative",

    // [Seller] Company Address
    "franchiseAddr" : "Company Address",

    // [Seller] Contact
    "franchiseTEL" : "070-1234-1234",

    // [Customer] Identification Number
    // └ Case1. {@no.7 tradeUsage} = ‘소득공제용’(To deduct income tax) : Resident Registration Number / Mobile / (e-Cashbill) Card Number / 010-000-1234 (for voluntary issuance)
    // └ Case2. {@no.7 tradeUsage} = ‘지출증빙용’(To proof the expenditure in business) : Business Registration Number / Resident Registration Number / Mobile / (e-Cashbill) Card Number
    // └ Do not enter hyphen('-')
    "identityNum" : "154420201111111111",

    // [Customer] Name
    "customerName" : "Name of customer",

    // Item Name
    "itemName" : "Item Name",

    // Order Number
    "orderNumber" : "Order Number",

    // [Customer] Email address
    // └ To send a notification mail
    "email" : "test@test.com",

    // [Customer] Mobile
    // └ When {@no.26 smssendYN} = ‘true’, a message is sent to this value
    "hp" : "010-111-222",

    // Whether user send a notification message
    // └ true = send, false = not send(Default)
    "smssendYN" : false,

    // Title of notification mail sent to customer
    // └ Default: a title POPBiLL set in advance
    "emailSubject" : ""
}

{
    // Cashbill ID
    "mgtKey" : "20221103-002",

    // The date and time of trading (Format : yyyyMMddHHmmss)
    // └ Valid Value : Today(Default) / Previous day
    "tradeDT" : "20221102000000",

    // NTS Confirm Number of the Original
    // └ Refer {@no.18 confirmNum} of [A.9. CashbillInfo] returned by [2.1. GetInfo]
    "orgConfirmNum" : "TB0000118",

    // Trade Date of the Original
    // └Refer {@no.3 tradeDate} of [A.9. CashbillInfo] returned by [2.1. GetInfo]
    "orgTradeDate" : "20221020",

    // Whether user send a notification message
    // └ true = send, false = not send(Default)
    // └ The message is sent to customer contact of the original
    "smssendYN" : false,

    // Memo for user’s convenience
    // └ invisible to customer
    "memo" : "메모",

    // Whether user revoke the whole of the trade or a part
    // └ true = a part, false = the whole(Default)
    "isPartCancel" : true,

    // Reason for revocation
    // └ Valid value: 1 / 2 / 3
    // └ 1 = Return(Default), 2 = Error on filed e-Cashbill, 3 = etc.
    "cancelType" : "1",

    // Supply values
    // └ When {@no.8 isPartCancel} is ‘true’, enter the value as much as to be revoked
    // └ When {@no.8 isPartCancel} is ‘false’, leave a blank
    "supplyCost" : "10000",

    // Tax Amount
    // └ When {@no.8 isPartCancel} is ‘true’, enter the value as much as to be revoked
    // └ When {@no.8 isPartCancel} is ‘false’, leave a blank
    "tax" : "1000",

    // Service Fee
    // └ When {@no.8 isPartCancel} is ‘true’, enter the value as much as to be revoked
    // └ When {@no.8 isPartCancel} is ‘false’, leave a blank
    "serviceFee" : "0",

    // Total Amount
    // └ Sum of {@no.10 supplyCost}, {@no.11 tax} and {@no.12 serviceFee}
    // └ When {@no.8 isPartCancel} is ‘true’, enter the value as much as to be revoked
    // └ When {@no.8 isPartCancel} is ‘false’, leave a blank
    "totalAmount" : "11000",

	// Title of notification mail sent to customer
    // └ Default: a title POPBiLL set in advance
    "emailSubject" : ""
}
