/*
* Object of e-Statement (Statement Type : Transaction details)
*
* A more detailed explanation can be found from the link below.
* - https://docs.popbill.com/en/statement/java/api#Statement
*/

{
    // Date of preparation, Format : yyyyMMdd
    "writeDate": "20201116",

    // Type of the total amount
    // └ Valid Value : 영수 / 청구
    // └ 영수 = Amount paid, 청구 = Amount due
    "purposeType": "영수",

    // Taxation Type
    // └ Valid Value : 과세 / 영세 / 면세
    // └ 과세 = Taxable, 영세 = Zero-rate, 면세 = Exempted
    "taxType": "과세",

    // Code for customized template 
    // └ Default : Applying POPBiLL default template
    "formCode": "",

    // Type of the e-Statement
    // └ Valid Value : 121 / 122 / 123 / 124 / 125 / 126
    // └ 121 = Transaction details, 122 = Bill, 123 = Estimate, 124 = Purchase order, 125 = Deposit slip, 126 = Receipt
    "itemCode": 121,

    // Statement ID assigned by partner(user)
    // └ An unique value to combine English letter, numeric and hyphen('-') *No redundancy
    "mgtKey": "20201116-06",

    // Title of notification mail sent to customer 
    // - Default: a title POPBiLL set in advance
    "emailSubject": "",

    /*************************************************************************
    *                             Supplier Information
    **************************************************************************/

    // [Supplier] Business registration number
    // └ 10 digits number except hyphen(‘-’)
    "senderCorpNum": "1208184429",

    // [Supplier] Company name
    "senderCorpName": "Supplier Company name",

    // [Supplier] Address
    "senderAddr": "Supplier Address",

    // [Supplier] Representative name
    "senderCEOName": "Supplier Representative name",

    // [Supplier] Identification Number for minor place of business
    // └ 4 digits number
    "senderTaxRegID": "",

    // [Supplier] Business Item
    "senderBizClass": "Supplier Business Item",

    // [Supplier] Business Type
    "senderBizType": "Supplier Business Type",

    // [Supplier] Name of the person in charge
    "senderContactName": "Supplier Name person in charge",

    // [Supplier] Email of the person in charge
    "senderEmail": "test@test.com",

    // [Supplier] Contact of the person in charge
    "senderTEL": "070-4304-2991",

    /*************************************************************************
    *                         Customer Infomation
    **************************************************************************/

    // [Customer] Business registration number
    // └ 10 digits number except hyphen(‘-’)
    "receiverCorpNum": "8888888888",

    // [Customer] Company name
    "receiverCorpName": "Customer Company name",

    // [Customer] Representative name
    "receiverCEOName": "Customer Representative name",

    // [Customer] Address
    "receiverAddr": "Customer Address",

    // [Customer] Identification Number for minor place of business
    // └ 4 digits number
    "recieverTaxRegID": "",

    // [Customer] Business Item
    "receiverBizClass": "Customer Business Item",

    // [Customer] Business Type
    "receiverBizType": "Customer Business Type",

    // [Customer] Name of the person in charge
    "receiverContactName": "Customer Name person in charge",

    // [Customer] Email of the person in charge
    "receiverEmail": "test@test.com",

    // [Customer] Contact of the person in charge
    "receiverTEL": "070-1111-2222",

    /*************************************************************************
    *                        e-Statement Details
    **************************************************************************/

    // Sum of supply values
    // └ Only positive(+)/negative(-) numbers and – (minus sign) are available
    "supplyCostTotal": "20000",

    // Sum of tax amount
    // └ Only positive(+)/negative(-) numbers and – (minus sign) are available
    "taxTotal": "2000",

    // Total Amount
    // └ sum of {no.9 taxTotal} and {no.10 supplyCostTotal}
    // └ Only positive(+)/negative(-) numbers and – (minus sign) are available
    "totalAmount": "22000",

    // Serial Number
    "serialNum": "1",

    // 1st Remark
    "remark1": "1st Remark",

    // 2nd Remark
    "remark2": "2nd Remark",

    // 3rd Remark
    "remark3": "3rd Remark",

    // Whether you attach registered business license
    // └ true : attach, false : not attach(Default)
    "businessLicenseYN": false,

    // 	Whether you attach registered copy of bankbook
    // └ true : attach, false : not attach(Default)
    "bankBookYN": false,


    /*************************************************************************
    *          	[List] Detailed list of items (※ No length limit)
    **************************************************************************/
    "detailList": [
        {
            // Serial Number
            // └ Must write the number in a row starting from 1
            "serialNum": 1,

            // Item name
            "itemName": "Item name - 01",

            // Date of preparation
            // └ Format : yyyyMMdd
            "purchaseDT": "20201116",

            // Quantity
            // └ Available to two decimal places
            // └ Minus(Negative) amount is available
            "qty": "1",

            // Unit price
            // └ Available to two decimal places
            // └ Minus(Negative) amount is available
            "unitCost": "10000",

            // Specification
            "spec": "Item's Specification",

            // Supply values
            // └ Unavailable to decimal places
            // └ Minus(Negative) amount is available
            // └ Below the decimal point is rounded off
            "supplyCost": "10000",

            // Tax amount
            // └ Unavailable to decimal places
            // └ Minus(Negative) amount is available
            // └ Below the decimal point is rounded off
            "tax": "1000",

            // Item's Remark
            "remark": "Item's remark - 01"
        },
        {
            // Serial Number
            // └ Must write the number in a row starting from 1
            "serialNum": 2,

            // Item name
            "itemName": "Item name - 02",

            // Date of preparation
            // └ Format : yyyyMMdd
            "purchaseDT": "20201116",

            // Quantity
            // └ Available to two decimal places
            // └ Minus(Negative) amount is available
            "qty": "1",

            // Unit price
            // └ Available to two decimal places
            // └ Minus(Negative) amount is available
            "unitCost": "10000",

            // Specification
            "spec": "Item's Specification",

            // Supply values
            // └ Unavailable to decimal places
            // └ Minus(Negative) amount is available
            // └ Below the decimal point is rounded off
            "supplyCost": "10000",

            // Tax amount
            // └ Unavailable to decimal places
            // └ Minus(Negative) amount is available
            // └ Below the decimal point is rounded off
            "tax": "1000",

            // Item's Remark
            "remark": "Item's remark - 02"
        }
    ],

    /*****************************************************************************
    *                         Additional Property
    * - Check Detail : https://docs.popbill.com/en/statement/propertyBag?lang=java
    ******************************************************************************/

    "propertyBag": {
        "Balance": "2000",     // Previous Balance
        "Deposit": "500",      // Deposit Amount
        "CBalance": "2500"     // Current Balance
    }
}

{
    // 발신 번호
    "sendNum" : "070111222",

    // 수신 번호
    "receiveNum" : "010111222",

    // [필수] 기재상 작성일자
    "writeDate" : "20211130",

    // [필수] 과세형태, (과세, 영세, 면세) 중 기재
    "taxType" : "과세",

    // [필수] (영수, 청구) 중 기재
    "purposeType" : "영수",

    // 전자명세서 문서유형 : 121 / 122 / 123 / 124 / 125 / 126 중 택 1
    // └ 121 = 거래명세서, 122 = 청구서, 123 = 견적서, 124 = 발주서, 125 = 입금표, 126 = 영수증
    "itemCode" : "121",

    // 전자명세서 관리를 위해 파트너가 할당하는 식별번호
    // └ 영문 대소문자, 숫자, 특수문자('-','_')만 이용 가능 
    "mgtKey" : "20211130_100",

    /************************************************************
     *                         공급자 정보
     ************************************************************/

    "senderCorpNum" : "1234567890",
    "senderTaxRegID" : "",
    "senderCorpName" : "테스트 상호",
    "senderCEOName" : "테스트 대표",
    "senderAddr" : "테스트 주소",
    "senderBizType" : "테스트 업태",
    "senderBizClass" : "테스트 종목",
    "senderContactName" : "테스트 담당자 성명",
    "senderDeptName" : "테스트 부서명",
    "senderTEL" : "070-111-222",
    "senderHP" : "010-000-222",
    "senderEmail" : "test@test.com",
    
    /************************************************************
     *                     공급받는자 정보
     ************************************************************/

    "receiverCorpNum" : "8888888888",
    "receiverTaxRegID" : ""	,
    "receiverCorpName" : "공급받는자 상호",
    "receiverCEOName" : "공급받는자 대표자 성명",
    "receiverAddr" : "공급받는자 주소",
    "receiverBizClass" : "공급받는자 업종",
    "eceiverBizType" : "공급받는자 업태",
    "receiverContactName" : "공급받는자 담당자명",
    "receiverTEL" : "010-0000-1111",
    "receiverHP" : "010-1111-2222",
    // 팝빌 개발환경에서 테스트하는 경우에도 안내 메일이 전송되므로,
    // 실제 거래처의 메일주소가 기재되지 않도록 주의
    "receiverEmail" : "test@test.com",

    /************************************************************
     *                   전자명세서 기재정보
     ************************************************************/

    "supplyCostTotal" : "200000",			// [필수] 공급가액 합계
    "taxTotal" : "20000",					// [필수] 세액 합계
    "totalAmount" : "220000",				// [필수] 합계금액 (공급가액 합계+세액합계)

    "serialNum" : "123",					// 기재상 일련번호 항목
    "remark1" : "비고1",
    "remark2" : "비고2",
    "remark3" : "비고3",

    "businessLicenseYN" : false,			//사업자등록증 첨부 여부
    "bankBookYN" : false,					//통장사본 첨부 여부
    "smssendYN" : false,					//발행시 안내문자 전송여부

    "detailList": [
        {
            // 일련번호
            // └ 1부터 순차적으로 입력 (최대 99)
            "serialNum": 1,

            // 거래일자
            // └ 형식 : yyyyMMdd
            "purchaseDT": "20201006",

            // 품명
            "itemName": "Item Name 01",

            // 규격
            "spec": "spec",

            // 수량
            // └ 소수점 아래 2번째 자리까지 입력 가능
            // └ 마이너스 입력 가능
            "qty": "1",

            // 단가
            // └ 소수점 아래 2번째 자리까지 입력 가능
            // └ 마이너스 금액 입력 가능
            "unitCost": "110000",

            // 공급가액
            // └ 정수만 입력 가능
            // └ 마이너스 금액 입력 가능, 소수점 아래 자동 절사
            "supplyCost": "100000",

            // 세액
            // └ 정수만 입력 가능
            // └ 마이너스 금액 입력 가능, 소수점 아래 자동 절사
            "tax": "10000",

            // 비고
            "remark": "Item reamrk"
        },
        {
            "serialNum": 2,
            "purchaseDT": "20201006",
            "itemName": "Item Name 02",
            "spec": "spec",
            "qty": "1",
            "unitCost": "110000",
            "supplyCost": "100000",
            "tax": "10000",
            "remark": "Item reamrk"
        }
    ]
}
