/*
*
* Object of e-Tax invoice
*
* A more detailed explanation can be found from the link below.
* - https://docs.popbill.com/en/taxinvoice/java/api#Taxinvoice
*
*/

{
    //	Whether user permits to issue an overdued e-Tax invoice or not
    // └ false = Do not (default) , true = Do
    "forceIssue" : false,

    // Title of notification mail sent to customer 
    // - Default: a title POPBiLL set in advance
    "emailSubject": "",

    // Memo for user's convenience 
    // - invisible to customer
    "memo": "RegistIssue Memo",

    // Date of preparation 
    // - Format : yyyyMMdd
    "writeDate": "20210629",

    // Charging Direction 
    // - Valid Value : 정과금 = Paid by seller
    "chargeDirection": "정과금",

    // Issuance Type - Valid Value : 정발행 = e-Tax invoice
    "issueType": "정발행",

    // Taxation Type
    // └ Valid Value : 과세 / 영세 / 면세
    // └ 과세 = Taxable, 영세 = Zero-rate, 면세 = Exempted
    "taxType": "과세",

    // [Seller] Business Registration Number
    // └ 10 digits number except hyphen('-')
    "invoicerCorpNum": "1234567890",

    // [Seller] Invoice ID assigned by user (No redundancy)
    // └ An unique value to combine English letter, number, hyphen('-')
    "invoicerMgtKey": "20210629-03",

    // [Seller] Identification Number for minor place of business
    // └ 4 digits number
    "invoicerTaxRegID": "",

    // [Seller] Name of Company
    "invoicerCorpName": "Seller Company Name",

    // [Seller] Name of Representative
    "invoicerCEOName": "Seller Company's CEO Name",

    // [Seller] Company Address
    "invoicerAddr": "Seller Address",

    // [Seller] Business Item
    "invoicerBizClass": "Seller Business Class",

    // [Seller] Business Type
    "invoicerBizType": "Seller Business Type",

    // [Seller] Name of the person in charg
    "invoicerContactName": "Business Contractor Name",

    // [Seller] Telephone of the person in charge
    "invoicerTEL": "070-4304-2991",

    // [Seller] Mobile of the person in charge
    "invoicerHP": "010-111-222",

    // [Seller] Email address of the person in charge
    "invoicerEmail": "seller@test.com",

    // [Seller] Whether you send a notification SMS or not to buyer's mobile(no. 48 invoiceeHP1) issuing an e-Tax invoice.
    // └ true = send, false = not send
    // └ Fee is charged to every SMS and is refunded if sending fails.
    "invoicerSMSSendYN": false,

    // Buyer Type
    // └ Valid Value : 사업자 / 개인 / 외국인
    // └ 사업자 = company, 개인 = individual, 외국인 = foreigner
    "invoiceeType": "사업자",

    // [Buyer] Business Registration Number
    // └ It must be synchronized with {no. 36 invoiceeType}
    // └ Company: Business registration number (10 digits except '-')
    // └ Individual: Resident registration number (13 digits except '-')
    // └ Foreigner: "9999999999999"(13 digits except '-')
    "invoiceeCorpNum": "8888888888",

    // [Buyer] Identification Number for minor place of business
    // └ 4 digits number
    "invoiceeTaxRegID": "",

    // [Buyer] Name of Company
    "invoiceeCorpName": "Buyer Corp Name",

    // [Buyer] Name of Representative
    "invoiceeCEOName": "Buyer Corp CEO Name",

    // [Buyer] Company Address
    "invoiceeAddr": "Buyer Address",

    // [Buyer] Business Item
    "invoiceeBizClass": "Buyer Business Class",

    // [Buyer] Business Type
    "invoiceeBizType": "Buyer Business Type",

    // [Buyer] Name of the person in charge
    "invoiceeContactName1": "Buyer Contacter Name",

    // [Buyer] Telephone of the person in charge
    "invoiceeTEL1": "070-111-222",

    // [Buyer] Mobile of the person in charge
    "invoiceeHP1": "010-222-333",

    // [Buyer] Email address of the person in charge
    "invoiceeEmail1": "test@test.com",

    // It's only requires to 'requested e-Tax invoice'
    "invoiceeSMSSendYN": null,



    // Sum of supply values
    // └ Only positive(+)/negative(-) numbers and – (minus sign) are available
    "supplyCostTotal": "200000",

    // Sum of tax amount
    // └ Only positive(+)/negative(-) numbers and – (minus sign) are available
    "taxTotal": "20000",

    // Total Amount
    // └ sum of {no.10 supplyCostTotal} and {no.11 taxTotal}
    // └ Only positive(+)/negative(-) numbers and – (minus sign) are available
    "totalAmount": "220000",

    // Modification Code
    // └ Mandatory value for revised (tax) invoice
    // └ Valid Value : 1 / 2 / 3 / 4 / 5 / 6
    // 1 = Correction of errors, 2 = Change in supply values, 3 = Return,
    // 4 = Cancellation of contract, 5 = Post opening of local letter of credit, 6 = Duplicated issuance
    "modifyCode": null,

    // Type of the total amount
    // └ Valid Value : 영수 / 청구
    // └ 영수 = Amount paid, 청구 = Amount due
    "purposeType": "영수",

    // One of e-Tax invoice's items to manage document – 'Serial number'
    "serialNum": "",

    // Payment Method – 'cash'
    "cash": "",

    // Payment Method – 'check'
    "chkBill": "",

    // Payment Method – 'credit'
    "credit": "",

    // Payment Method – 'note'
    "note": "",

    // 1st Remark
    // └ Mandatory in case of {no 36. invoiceeType} is "외국인" (foreigner).
    // └ Need to put 'Alien Registration Number' or 'Passport Number'.
    "remark1": "remark 1",

    // 2nd Remark
    "remark2": "remark 2",

    // 3rd Remark
    "remark3": "remark 3",

    // Whether you attach registered business license
    // └ true : attach, false : not attach(Default)     
    "businessLicenseYN": false,

    // Whether you attach registered copy of bankbook
    // └ true : attach, false : not attach(Default)
    "bankBookYN": false,

    // [List] Detailed list of items (Maximum : 99 lists)
    "detailList": [
        {
            // Serial Number
            // └ Must write the number in a row starting from 1 (Maximum : 99)
            "serialNum": 1,

            // Date of preparation
            // └ Format : yyyyMMdd
            "purchaseDT": "20201006",

            // Item name
            "itemName": "Item Name 01",

            // Specification
            "spec": "spec",

            // Quantity
            // └ Available to two decimal places
            // └ Minus(Negative) amount is available
            "qty": "1",

            // Unit price
            // └ Available to two decimal places
            // └ Minus(Negative) amount is available
            "unitCost": "110000",

            // Supply values
            // └ Unavailable to decimal places
            // └ Minus(Negative) amount is available
            // └ Below the decimal point is rounded off
            "supplyCost": "100000",

            // Tax amount
            // └ Unavailable to decimal places
            // └ Minus(Negative) amount is available
            // └ Below the decimal point is rounded off
            "tax": "10000",

            // Remark
            "remark": "Item reamrk"
        },
        {
            "serialNum": 2,
            "purchaseDT": "20201006",
            "itemName": "Item Name 02",
            "spec": "spec",
            "qty": "1",
            "unitCost": "110000",
            "supplyCost": "100000",
            "tax": "10000",
            "remark": "Item reamrk"
        }
    ],

    // [List] To send notification mails to multiple person in charge (Maximum : 5)
    "addContactList": [
        {
            // Serial Number
            // └ Must write the number in a row starting from 1 (Maximum : 5)
            "serialNum": 1,

            // Email
            "email": "test@test.com",

            // Name of the person in charge
            "contactName": "Buyer Contacter Addtion 01"
        },
        {
            // Serial Number
            // └ Must write the number in a row starting from 1 (Maximum : 5)
            "serialNum": 2,

            // Email
            "email": "test@test.com",

            // Name of the person in charge
            "contactName": "Buyer Contacter Addtion 02"
        }
    ]
}
